mapper.select:
SELECT a.id id, a.theme_number number, a.price price, a.status status, b.series_id, b.id theme_id, b.theme_name theme_name, b.theme_pic theme_pic, b.theme_type type, a.version version FROM star_nft_theme_number a INNER JOIN star_nft_theme_info b ON a.series_theme_info_id = b.id
b.theme_type = #{themeType}
AND b.series_id = #{seriesId}
AND b.id = #{themeId}
AND a.is_delete = 0 AND a.owner_by is null AND a.status = 0 AND b.is_delete = 0

SELECT a.id id, a.theme_number number, a.price price, a.status status, b.id theme_id, b.theme_name theme_name, b.theme_pic theme_pic, b.theme_type type FROM star_nft_theme_number a INNER JOIN star_nft_theme_info b ON a.series_theme_info_id = b.id
b.theme_type = #{themeType}
AND b.series_id = #{seriesId}
AND b.id = #{themeId}
AND b.theme_name LIKE CONCAT('%', #{themeName}, '%')
AND a.is_delete = 0 AND b.is_delete = 0 AND a.status = 2
ORDER BY a.${orderBy} ${sortType}

mapper.update:
update star_nft_theme_number
set owner_by = #{userId},
    `status` = #{code},
    version  = #{version} + 1
where id = #{id}
  and version = #{version}

mapper.select:
select price
from star_nft_theme_number
where `status` = 2
and is_delete = 0
and series_theme_info_id = #{themeId}

   and price <= #{median}

mapper.update:
UPDATE star_nft_theme_number
SET `status` = 1
WHERE
    series_theme_info_id = #{themeId}
  AND is_delete = 0
  AND `status` = 2;
