mapper.select:
SELECT s.theme_name AS theme_name, series_theme_info_id AS theme_id, theme_pic AS theme_images, COUNT(1) AS nums
FROM star_nft_user_theme u
         LEFT JOIN star_nft_theme_info s ON u.series_theme_info_id = s.id
WHERE u.user_id = #{userId}
  AND u.`status` = #{status}
  AND u.source = #{themeType}
  AND u.series_id = #{seriesId}
  AND u.is_delete = 0
GROUP BY u.series_theme_info_id
