select.where:
is_deleted = 0
 and account = #{account}
 and password = #{password}
 and phone = #{phone}
 and nick_name like concat('%', #{nickName}, '%')
 and avatar = #{avatar}
 and ply_password = #{plyPassword}
 and is_active = #{isActive}
 and full_name like concat('%', #{fullName}, '%')
 and id_number = #{idNumber}
 and real_person_flag = #{realPersonFlag}

 and created_at = #{createdAt}
 and created_by = #{createdBy}
 and modified_at = #{modifiedAt}
 and modified_by = #{modifiedBy}

mapper.update:
update account_user
account = #{account}, password = #{password}, phone = #{phone}, nick_name = #{nickName}, avatar = #{avatar}, ply_password = #{plyPassword}, is_active = #{isActive}, full_name = #{fullName}, id_number = #{idNumber}, real_person_flag = #{realPersonFlag}, is_deleted = #{isDeleted}, created_at = #{createdAt}, created_by = #{createdBy}, modified_at = #{modifiedAt}, modified_by = #{modifiedBy},
where id = #{id}
