select.where:
is_delete = 0
 and series_id = #{seriesId}
 and theme_type = #{themeType}
 and theme_name like concat('%', #{themeName}, '%')
 and theme_pic = #{themePic}
 and publish_number = #{publishNumber}
 and descrption = #{descrption}
 and theme_level = #{themeLevel}
 and stock = #{stock}
 and lssue_price = #{lssuePrice}
 and create_at = #{createAt}
 and update_at = #{updateAt}

 and is_recommend = #{isRecommend}
 and restrict_number = #{restrictNumber}
 and tags = #{tags}
 and contract_address = #{contractAddress}
 and publisher_id = #{publisherId}
 and market_open_time = #{marketOpenTime},
 and is_resale = #{isResale},

mapper.update:
update star_nft_theme_info
series_id = #{seriesId}, theme_type = #{themeType}, theme_name = #{themeName}, theme_pic = #{themePic}, publish_number = #{publishNumber}, descrption = #{descrption}, theme_level = #{themeLevel}, stock = #{stock}, lssue_price = #{lssuePrice}, create_at = #{createAt}, create_by = #{createBy}, update_at = #{updateAt}, update_by = #{updateBy}, is_delete = #{isDelete}, is_recommend = #{isRecommend}, restrict_number = #{restrictNumber}, tags = #{tags}, contract_address = #{contractAddress}, publisher_id = #{publisherId}, market_open_time = #{marketOpenTime}, is_resale = #{isResale},
where id = #{id}

mapper.select:
    select t.id           as id,
       t.series_id    as seriesId,
       s.series_name  as seriesName,
       t.theme_type   as themeType,
       t.theme_name   as themeName,
       t.theme_pic    as themePic,
       t.publish_number   as publishNumber,
       t.descrption,
       t.theme_level  as themeLevel,
       t.stock,
       t.lssue_price  as lssuePrice,
       t.create_at    as createAt,
       t.create_by    as createBy,
       t.update_at    as updateAt,
       t.update_by    as updateBy,
       t.is_delete    as isDelete,
       t.is_recommend as isRecommend,
       t.restrict_number  as restrictNumber,
       t.tags,
       t.contract_address as contractAddress,
       t.market_open_time as marketOpenTime,
       t.publisher_id as publisherId,
       p.publisher as publisher,
       p.pic as pic,
       t.is_resale as isResale
from star_nft_theme_info t
    left join star_nft_series s
on t.series_id = s.id
    left join star_nft_publisher p
on p.id = t.publisher_id

    t.is_delete = 0
     and t.series_id = #{seriesId}
     and t.theme_type = #{themeType}
     and t.theme_name like concat('%', #{themeName}, '%')
     and t.theme_pic = #{themePic}
     and t.publish_number = #{publishNumber}
     and t.descrption = #{descrption}
     and t.theme_level = #{themeLevel}
     and t.stock = #{stock}
     and t.lssue_price = #{lssuePrice}
     and t.create_at = #{createAt}
     and t.update_at = #{updateAt}

     and t.is_recommend = #{isRecommend}
     and t.restrict_number = #{restrictNumber}
     and t.tags = #{tags}
     and t.contract_address = #{contractAddress}
     and t.publisher_id = #{publisherId}
     and market_open_time = #{marketOpenTime}
     and is_resale = #{isResale}
