select.where:
is_deleted = 0
 and is_deleted = #{isDeleted}
 and created_at = #{createdAt}
 and created_by = #{createdBy}
 and modified_at = #{modifiedAt}
 and modified_by = #{modifiedBy}
 and seller_id = #{sellerId}
 and spu_id = #{spuId}
 and sku_id = #{skuId}
 and stock = #{stock}
 and goods_num = #{goodsNum}
 and sec_cost = #{secCost}
 and start_time between #{params.beginStartTime} and #{params.endStartTime}
 and end_time between #{params.beginEndTime} and #{params.endEndTime}
 and goods_desc = #{goodsDesc}
 and status = #{status}
 and version = #{version}

mapper.update:
update star_schedule_seckill
is_deleted = #{isDeleted}, created_at = #{createdAt}, created_by = #{createdBy}, modified_at = #{modifiedAt}, modified_by = #{modifiedBy}, seller_id = #{sellerId}, spu_id = #{spuId}, sku_id = #{skuId}, stock = #{stock}, goods_num = #{goodsNum}, sec_cost = #{secCost}, start_time = #{startTime}, end_time = #{endTime}, goods_desc = #{goodsDesc}, status = #{status}, version = #{version},
where id = #{id}

mapper.select:
select k.id         as  id,
       k.is_deleted as isDelete,
       k.created_at as createAt,
       k.created_by as createBy,
       k.modified_at as modifiedAt,
       k.modified_by as modifiedBy,
       k.seller_id  as selleId,
       p.publisher  as publisher,
       p.pic        as publisherPic,
       k.spu_id     as spuId,
       i.theme_name as themeName,
       i.theme_pic  as themePic,
       k.sku_id     as skuId,
       k.stock      as stock,
       k.goods_num  as goodsNum,
       k.sec_cost   as secCost,
       k.start_time as startTime,
       k.end_time   as endTime,
       k.goods_desc as goodDesc,
       k.status     as status,
       k.version    as version
from star_schedule_seckill k
    left join star_nft_publisher p
on k.seller_id =  p.id
    left join star_nft_theme_info i
on k.spu_id = i.id

    is_deleted = 0
     and created_at = #{createdAt}
     and created_by = #{createdBy}
     and modified_at = #{modifiedAt}
     and modified_by = #{modifiedBy}
     and seller_id = #{sellerId}
     and spu_id = #{spuId}
     and sku_id = #{skuId}
     and stock = #{stock}
     and goods_num = #{goodsNum}
     and sec_cost = #{secCost}
     and start_time between #{params.beginStartTime} and #{params.endStartTime}
     and end_time between #{params.beginEndTime} and #{params.endEndTime}
     and goods_desc = #{goodsDesc}
     and status = #{status}
     and version = #{version}
    order by k.modified_by desc
